MSIG_CSS = """
:root{
  /* Brand */
  --msig-red:#E4002B;
  --msig-navy:#1F2A5A;
  --msig-navy-light:#2A3A74;

  /* Dark tokens for SIDEBAR (unchanged) */
  --bg:#0B1023;            /* sidebar/background outside main */
  --panel:#141A33;
  --panel-2:#0F1530;
  --border:#232B4A;
  --text:#E6EAF2;
  --muted:#AAB2C7;
  --link:#9DB0FF;

  /* Light tokens for MAIN content */
  --main-bg:#FFFFFF;
  --main-text:#111111;
  --main-border:#E9ECF5;
  --main-link:#1F2A5A;

  /* Chat bubbles (both) */
  --bubble-bg:#1F2A5A;     /* blue bubbles */
  --bubble-text:#FFFFFF;
}

/* --- Global keeps the previous dark (so sidebar stays dark) --- */
html, body, .stApp { background: var(--bg) !important; color: var(--text) !important; }

/* Remove the dark header/footers/bars so the main blends cleanly */
header .stAppHeader{ background: transparent !important; border-bottom: none !important; }
footer, .stApp footer { background: transparent !important; }
[data-testid="stDecoration"]{ background: transparent !important; }

/* --- SIDEBAR remains dark --- */
[data-testid="stSidebar"] {
  background: var(--bg) !important;
  color: var(--text) !important;
}
[data-testid="stSidebar"] [data-testid="stFileUploader"] > section{
  border: 2px dashed var(--border) !important;
  background: var(--panel) !important;
  border-radius: 16px !important;
}

/* --- MAIN CONTENT ONLY (white background + dark text) --- */
[data-testid="stAppViewContainer"] .main{
  background: var(--main-bg) !important;
  color: var(--main-text) !important;
}
[data-testid="stAppViewContainer"] .main .block-container{
  background: var(--main-bg) !important;
}
[data-testid="stAppViewContainer"] .main h1,
[data-testid="stAppViewContainer"] .main h2,
[data-testid="stAppViewContainer"] .main h3,
[data-testid="stAppViewContainer"] .main p,
[data-testid="stAppViewContainer"] .main li,
[data-testid="stAppViewContainer"] .main .stMarkdown,
[data-testid="stAppViewContainer"] .main .stCaption{
  color: var(--main-text) !important;
}
[data-testid="stAppViewContainer"] .main a{ color: var(--main-link) !important; }
[data-testid="stAppViewContainer"] .main a:hover{ color: var(--msig-red) !important; }

/* Buttons in MAIN */
[data-testid="stAppViewContainer"] .main .stButton > button{
  background: var(--msig-red) !important;
  color: #FFFFFF !important;
  border: 0 !important;
  border-radius: 12px !important;
  box-shadow: 0 6px 14px rgba(228,0,43,.18);
}

/* Inputs in MAIN */
[data-testid="stAppViewContainer"] .main textarea,
[data-testid="stAppViewContainer"] .main input,
[data-testid="stAppViewContainer"] .main .stTextInput > div > div > input{
  background: var(--main-bg) !important;
  color: var(--main-text) !important;
  border: 1px solid var(--main-border) !important;
  border-radius: 12px !important;
}

/* Generic cards/tables in MAIN */
[data-testid="stAppViewContainer"] .main .st-cx,
[data-testid="stAppViewContainer"] .main .stDataFrame,
[data-testid="stAppViewContainer"] .main .stTable,
[data-testid="stAppViewContainer"] .main .stCodeBlock{
  background: var(--main-bg) !important;
  color: var(--main-text) !important;
  border: 1px solid var(--main-border) !important;
  border-radius: 12px !important;
}

/* --- CHAT in MAIN: blue bubbles, assistant outlined RED --- */
[data-testid="stAppViewContainer"] .main [data-testid="stChatMessage"]{
  background: var(--bubble-bg) !important;     /* blue */
  color: var(--bubble-text) !important;
  border: 2px solid var(--msig-navy) !important;  /* default navy outline */
  border-radius: 14px !important;
}
[data-testid="stAppViewContainer"] .main [data-testid="stChatMessage"] [data-testid="stMarkdownContainer"] *{
  color: var(--bubble-text) !important;
}
/* Assistant/BOT gets red outline all around */
[data-testid="stAppViewContainer"] .main [data-testid="stChatMessage"]:has([data-testid="assistant-avatar"]){
  border-color: var(--msig-red) !important;     /* SOLID RED OUTLINE */
}
/* User keeps navy outline */
[data-testid="stAppViewContainer"] .main [data-testid="stChatMessage"]:has([data-testid="user-avatar"]){
  border-color: var(--msig-navy) !important;
}

/* Chat input in MAIN */
[data-testid="stAppViewContainer"] .main [data-testid="stChatInput"] textarea{
  background: var(--main-bg) !important;
  color: var(--main-text) !important;
  border: 1.5px solid var(--main-border) !important;
  border-radius: 12px !important;
}
[data-testid="stAppViewContainer"] .main [data-testid="stChatInput"] button{
  background: var(--msig-red) !important;
  color: #FFFFFF !important;
  border-radius: 12px !important;
}
"""
